'use strict'

//Part 1
function calcCode(instructions) {
	let starting = [1,1]
  let answer = []
  let importId = new Set();
  const keypad = [[1,2,3],[4,5,6],[7,8,9]]
  const mapDirection = {
    'L': [ 0,  -1 ],
    'R': [  0,  1 ],
    'U': [  -1,  0 ],
    'D': [  1,  0 ]
  }
	const directionsArr = instructions.split('\n').map(x => x.split(''))
  let important = directionsArr.map(arr => arr.length)
  for (let i=0; i<important.length; i++) {
    important[i] = important[i-1] ? important[i-1] + important[i] : important[i]-1;
    importId.add(important[i])
  }

  let mapped = directionsArr.map(oneStep => oneStep.map(step => mapDirection[step] )).reduce((a,b) => a.concat(b), [])
  
  let result = mapped.reduce((current,direction,idx) => {
    let next =[]
    next[0] = current[0] + direction[0] >= 0 && current[0] + direction[0] < 3 ? current[0] + direction[0] : current[0]
    next[1] = current[1] + direction[1] >= 0 && current[1] + direction[1] < 3 ? current[1] + direction[1] : current[1]

    if (importId.has(idx)) {
      answer.push(keypad[next[0]][next[1]])
    }
    return next
  }, starting)

  return answer
  
}


